import "./style.css"
import React, { useEffect } from 'react';
const [isVisible, setIsVisible] = React.useState(false);

// Функция, которая будет вызываться при изменении видимости элемента
function onIntersect(entries) {
  entries.forEach((entry) => {
    if (entry.isIntersecting) {
      setIsVisible(true);
    }
  });
}

// Создаем новый экземпляр IntersectionObserver и передаем ему функцию обратного вызова
const observer = new IntersectionObserver(onIntersect);

// Находим элемент подвала и добавляем наблюдение за ним
const footerElement = document.querySelector('.Footer');
if (footerElement) {
  observer.observe(footerElement);
}
const Footer = () => {
   
    return ( 
        <div className="Footer">
            <p>podval</p>
        </div>
     );
}
 
export default Footer;
