import React, { useState } from 'react';
import main_page from "./../../img/main_page.png";
import main_page2 from "./../../img/main_page2.png";
import main_page3 from "./../../img/main_page3.png";

const Slider = () => {
    const [slideIndex, setSlideIndex] = useState(1);

    const showSlides = (n) => {
        let i;
        const slides = document.getElementsByClassName("slade");
        const dots = document.getElementsByClassName("dot");
        if (n > slides.length) { setSlideIndex(1) }
        if (n < 1) { setSlideIndex(slides.length) }
        for (i = 0; i < slides.length; i++) {
            slides[i].style.display = "none";
        }
        for (i = 0; i < dots.length; i++) {
            dots[i].className = dots[i].className.replace(" active", "");
        }
        slides[slideIndex - 1].style.display = "block";
        dots[slideIndex - 1].className += " active";
    }

    const prevslide = () => {
        setSlideIndex(slideIndex - 1);
        showSlides(slideIndex - 1);
    }

    const nextlide = () => {
        setSlideIndex(slideIndex + 1);
        showSlides(slideIndex + 1);
    }

    return (
        <>
            <div className="slideshow">
                <div className="slade">
                    <img src={main_page} alt="photo"></img>
                </div>
                <div className="slade">
                    <img src={main_page2} alt="photo2"></img>
                </div>
                <div className="slade">
                    <img src={main_page3} alt="photo3"></img>
                </div>
                <a className="prev" onClick={prevslide}>&#10094;</a>
                <a className="next" onClick={nextlide}>&#10095;</a>
                <div style={{ textAlign: "center" }}>
                    <span className="dot" onClick={() => currentSlide(1)}></span>
                    <span className="dot" onClick={() => currentSlide(2)}></span>
                    <span className="dot" onClick={() => currentSlide(3)}></span>
                </div>
            </div>
        </>
    );
}

export default Slider;
