import React, { useState } from 'react';
import istor from "./img/исторический.png";
import "./style.css";

const Musei = () => {
   const [hovered, setHovered] = useState([false, false, false, false]);

   const handleMouseEnter = (index) => {
       setHovered((prev) => {
           const newHovered = [...prev];
           newHovered[index] = true;
           return newHovered;
       });
   };

   const handleMouseLeave = (index) => {
       setHovered((prev) => {
           const newHovered = [...prev];
           newHovered[index] = false;
           return newHovered;
       });
   };

    return ( 
        <div className="Musei">

         <div className="t1">
            <p>Музеи Кубанского ГАУ</p>
         </div>

         <div className="t2">
            <p>На территории университета расположено 9 музеев и множество других памятников архитектуры, о которых Настя подготовит текст, а пока пару слов на латыни... Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.</p>
         </div>
         <div className="container">
         {[1, 2, 3, 4].map((num, index) => (
             <div className={`r${num}`} 
                  onMouseEnter={() => handleMouseEnter(index)} 
                  onMouseLeave={() => handleMouseLeave(index)}
                  style={{ background: hovered[index] ? '#287828' : '#61AA2A' }}
                  key={index}>
                <div className={`photo${num}`}>
                    <img src={istor} alt={`музей ${num}`}/>
                </div>
                <div className={`textm${num}`}>
                   <p>Музей {num}</p>
                </div>
             </div>
         ))}
        </div>
        </div>
     );
}
 
export default Musei;
