import React, { useState } from 'react';
import istor from "./img/исторический.png";
import "./style.css";

const Musei = () => {
    const [hoveredIndex, setHoveredIndex] = useState(null); // Состояние для индекса наведенной карточки

    const handleMouseEnter = (index) => {
        setHoveredIndex(index);
    };

    const handleMouseLeave = () => {
        setHoveredIndex(null);
    };

    // Массив данных для карточек
    const cardsData = [
        { title: "Исторический музей", image: istor },
        { title: "Музей 2", image: "" },
        // Добавьте другие карточки здесь по мере необходимости
    ];

    // Создание карточек с помощью цикла
    const cards = [];
    for (let i = 0; i < cardsData.length; i++) {
        const card = cardsData[i];
        cards.push(
            <div className="card" 
                 key={i}
                 onMouseEnter={() => handleMouseEnter(i)} 
                 onMouseLeave={handleMouseLeave}
                 style={{ background: hoveredIndex === i ? '#287828' : '#61AA2A' }}>
                <div className="photo">
                    <img src={card.image} alt={card.title}/>
                </div>
                <div className="text">
                   <p>{card.title}</p>
                </div>
             </div>
        );
    }

    return ( 
        <div className="Musei">

         <div className="t1">
            <p>Музеи Кубанского ГАУ</p>
         </div>

         <div className="t2">
            <p>На территории университета расположено 9 музеев и множество других памятников архитектуры, о которых Настя подготовит текст, а пока пару слов на латыни... Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.</p>
         </div>
         <div className="container">
            {cards}
        </div>
        </div>
     );
}
 
export default Musei;
