import React, { useState, useEffect } from 'react';
import Header from './../../components/Header/Header';
import Footer from './../../components/Footer/Footer';
import './style.css';

const images = [
  'path-to-your-image/main_page.png', // Make sure these paths are correct
  'path-to-your-image/main_page2.png',
  'path-to-your-image/main_page3.png',
  // ... add all the images you want to include in the slider
];

const University = () => {
    const [index, setIndex] = useState(0);

    useEffect(() => {
        const timer = setInterval(() => {
            setIndex((prevIndex) => (prevIndex + 1) % images.length);
        }, 3000); // Change image every 3 seconds

        return () => clearInterval(timer); // Clean up the interval on component unmount
    }, []);

    return (
        <>
            <Header />
            <div className="slider-container">
                <div className="slider">
                    {images.map((img, idx) => (
                        <div
                            className={`slide ${index === idx ? 'active' : ''}`}
                            key={idx}
                        >
                            <img src={img} alt={`Slide ${idx}`} />
                        </div>
                    ))}
                </div>
            </div>
            <Footer />
        </>
    );
}

export default University;
