import React, { useState } from 'react';
import Header from './../../components/Header/Header';
import Footer from './../../components/Footer/Footer';
import './University.css';

const images = [
  '/path-to-your-images/main_page.png',
  '/path-to-your-images/main_page2.png',
  '/path-to-your-images/main_page3.png',
  // ... add all the images you want to include in the slider
];

const University = () => {
  const [current, setCurrent] = useState(0);
  const length = images.length;

  const nextSlide = () => {
    setCurrent(current === length - 1 ? 0 : current + 1);
  };

  const prevSlide = () => {
    setCurrent(current === 0 ? length - 1 : current - 1);
  };

  if (!Array.isArray(images) || images.length <= 0) {
    return null; // if no images provided, don't render the slider
  }

  return (
    <>
      <Header />
      <div className="slider-container">
        <button className="left-arrow" onClick={prevSlide}>&lt;</button>
        <button className="right-arrow" onClick={nextSlide}>&gt;</button>
        {images.map((image, index) => (
          <div
            className={index === current ? 'slide active' : 'slide'}
            key={index}
          >
            {index === current && (
              <img src={image} alt="University campus" className="image" />
            )}
          </div>
        ))}
      </div>
      <Footer />
    </>
  );
}

export default University;
