import React, { useState, useEffect } from 'react';
import './../../components/Header/Header';
import './../../components/Footer/Footer';
import './University.css'; // Импорт стилей напрямую без использования CSS модулей

// Импорт изображений
import main_page2 from "./../../img/main_page2.png";
import main_page3 from "./../../img/main_page3.png";
import main_page4 from "./../../img/main_page4.png";
import main_page5 from "./../../img/main_page5.png";
import main_page6 from "./../../img/main_page6.png";


// Массив с адресами загруженных изображений
const images = [main_page2, main_page3, main_page4, main_page5, main_page6];

const University = () => {
    const [currentIndex, setCurrentIndex] = useState(0); // Состояние для текущего индекса изображения

    useEffect(() => {
        const timer = setInterval(() => {
            setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
        }, 3000);
        return () => clearInterval(timer);
    }, []);

    const goToNext = () => {
        setCurrentIndex((currentIndex) => (currentIndex + 1) % images.length);
    };

    const goToPrev = () => {
        setCurrentIndex((currentIndex) => (currentIndex - 1 + images.length) % images.length);
    };

    return (
        <div className="page-container">
            <h1 className="page-title">Слайдер с фотками универа</h1>
            <div className="slider-wrapper">
                <button onClick={goToPrev} className="slider-button">&#10094;</button>
                {images.map((img, index) => (
                    <div key={index} className={`slider-image-container ${index === currentIndex ? 'active' : ''}`}>
                        <img src={img} alt={`Slide ${index}`} className="slider-image" />
                    </div>
                ))}
                <button onClick={goToNext} className="slider-button">&#10095;</button>
            </div>
        </div>
    );
};

export default University;
